shows.owners:
function(doc, req) {
            return {
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(doc.owners)
            };
        }

lists.names:
function() {
        provides('json', function() {
          var names = [];
            while (row = getRow()) {
              names.push(row.id)
            }
            send(JSON.stringify(names));
          })
      }

lists.groups:
function() {
        provides('json', function() {
          var groups = [];
          while (row = getRow()) {
            groups.push(row.key.join('.'))
          }
          send(JSON.stringify(groups));
        })
      }
