lists.usernames:
function() {
            provides('json', function() {
                var usernames = [];
                while (row = getRow()) {
                    if (row.id.charAt(0) != '_') {
                        usernames.push(row.id)
                    }
                }
                send(JSON.stringify(usernames));
            })
        }

validate_doc_update:
function(newDoc, oldDoc, userCtx, secObj) {
        if (!newDoc.password) throw({forbidden: 'password required'});
    }
